import React, { useState } from "react";
import styled from "styled-components";
import Container from "../Components/Container";
import FormContainer from "../Components/FormContainer";

const ButtonWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding-top: 20px;

  @media (min-width: 917px) {
    flex-direction: row;
    justify-content: space-evenly;
  }
`;

const Button = styled.button`
  width: 90%;
  height: 40px;
  margin: 10px;

  @media (min-width: 917px) {
    width: 25%;
    height: auto;
  }
`;

const FormTextArea = styled.textarea`
  display: flex;
  width: 80%;
  margin: 5px 0;

  @media (min-width: 917px) {
    margin: 5px 30px;
  }
`;

const Health = () => {
  const [happyItems, setHappyItems] = useState([]);
  const [mehItems, setMehItems] = useState([]);
  const [sadItems, setSadItems] = useState([]);

  const addItem = (type, item) => {
    switch (type) {
      case "happy":
        happyItems.push(item);
        setHappyItems(happyItems);
        console.log(happyItems);
        break;
      case "meh":
        mehItems.push(item);
        setMehItems(mehItems);
        console.log(mehItems);
        break;
      case "sad":
      default:
        sadItems.push(item);
        setSadItems(sadItems);
        console.log(sadItems);
        break;
    }
  };

  return (
    <Container>
      <FormContainer action={addItem}>
        <FormTextArea id="description" name="description" cols="60" rows="5" />
        <ButtonWrapper>
          <Button onClick={() => addItem("happy", "happyData")}>
            Add to Happy Feedback
          </Button>
          <Button onClick={() => addItem("meh", "mehData")}>
            Add to Meh Feedback
          </Button>
          <Button onClick={() => addItem("sad", "sadData")}>
            Add to Sad Feedback
          </Button>
        </ButtonWrapper>
      </FormContainer>
    </Container>
  );
};

export default Health;
