import { useState, useEffect } from "react";

const helpers = () => {
  const [memberList, setMemberList] = useState(null);

  function useMemberList(members) {
    useEffect(() => {
      function updateMemberList(list) {
        setMemberList(list);
      }

      updateMemberList(members);
    });

    return memberList;
  }

  function getMembers() {
    return memberList;
  }
};

export default helpers;
