import React from "react";
import styled from "styled-components";
import Container from "../Components/Container";

const Hero = styled.h1`
  align-self: center;
  color: #f26c0d;
  font-family: Arial;
  font-weight: 700;
  font-size: 4vw;
  margin: 0 auto;
`;

const CopyText = styled.p`
  display: flex;
  flex-direction: column;
  align-self: flex-start;
  min-width: 140px;
  margin: 20px;
  width: 50%;
`;

const Image = styled.img`
  display: flex;
  margin: 5px;
  width: 40%;
  height: 30%;
  margin: 0 auto;
`;

const BodyContainer = styled(Container)`
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  align-content: center;
  margin-top: 20px;

  @media (min-width: 917px) {
    flex-direction: row;
  }
`;

const IFrame = styled.iframe`
  align-content: center;
  margin: 30px auto;
  width: 50%;
  height: auto;
`;

const Home = () => {
  return (
    <Container>
      <Hero>Sprint Retrospective</Hero>
      <BodyContainer>
        <Image
          src="https://scrumorg-website-prod.s3.amazonaws.com/drupal/inline-images/2017-03/SprintRetro.png"
          alt="typical model"
        />
        <CopyText>
          At my company, TheBash.com, we hold biweekly sprint retrospectives.
          During these meetings, members are given the opportunity to provide
          feedback about the most recent sprint. Feedback is given in the form
          of "I am (happy|meh|sad)...". As members provide feedback, other
          teammates can plus one (+1) that statement to show agreement or in
          order to ensure that the topic will be discussed.
          <br />
          <ul>
            <li>Happy - What worked well?</li>
            <li>Meh - What could be improved?</li>
            <li>Sad - What needs immediate discussion?</li>
          </ul>
          <br />
          During the discussion phase, we speak in greater detail about the Sad
          and Meh items. We vary in style, sometimes using the votes to decide
          the topics discussed, sometimes starting with the Sad comments then
          finish with Meh.
        </CopyText>
        <IFrame
          src="https://www.youtube.com/embed/bHfbeucM7Tg"
          frameBorder="0"
          allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
          allowFullScreen
        ></IFrame>
      </BodyContainer>
    </Container>
  );
};

export default Home;
